mapper.select:

        SELECT rownum rnum, a.*, b.*
        FROM MEMBERS a
        LEFT JOIN BREED b ON a.MEM_BR_IDX = b.BR_IDX


                AND MEM_ID LIKE '%'||#{id}||'%'


                AND MEM_DOG_NAME LIKE '%'||#{dogname}||'%'


    	) AA
			) AAA
			WHERE 
			    AAA.RNUM BETWEEN ${startIndex} AND ${startIndex+pageSize}


SELECT *
FROM BREED a
WHERE a.BR_IDX = #{BR_IDX}

mapper.update:

UPDATE BREED
SET
    BR_NAME = #{BR_NAME}
WHERE BR_IDX = #{BR_IDX}

mapper.insert:

INSERT INTO
    BREED(
          BR_IDX,
          BR_NAME
)
VALUES(
        seq_breed.nextval,
          #{BR_NAME}
         )

mapper.select:

        SELECT rownum rnum, br_idx, br_name
        FROM BREED b
        ORDER BY BR_IDX ASC
       ) AA
			) AAA
			WHERE 
			    AAA.RNUM BETWEEN ${startIndex} AND ${startIndex+pageSize}
